import axios from 'axios';
import {
    Storage
} from '@google-cloud/storage';
export default async function textToSpeech({
    text,
    voice_id,
    apiKey,
    fileName,
    optimize_streaming_latency = 0,
    output_format = 'mp3_44100_128',
}: NodeInputs, {
    logging
}: NodeScriptOptions) : NodeOutput  {
    try {
        const apiEndpoint =
            `https://api.elevenlabs.io/v1/text-to-speech/${voice_id}?optimize_streaming_latency=${optimize_streaming_latency}&output_format=${output_format}`;
        const requestBody = {
            text,
            model_id: 'eleven_monolingual_v1',
            voice_settings: {
                stability: 0,
                similarity_boost: 0,
                style: 0,
                use_speaker_boost: true,
            },
        };
        const response = await axios.post(apiEndpoint, requestBody, {
            headers: {
                'Content-Type': 'application/json',
                'xi-api-key': apiKey,
            },
            responseType: 'arraybuffer',
        });
        const storage = new Storage();
        const bucketName = process.env.BUCKET;
        await storage.bucket(bucketName).file(fileName).save(response.data, {
            metadata: {
                contentType: 'audio/mpeg',
                cacheControl: 'no-cache, max-age=0',
                acl: [{
                    entity: 'allUsers',
                    role: storage.acl.READER_ROLE,
                }],
            },
        });
        const publicUrl = `https://storage.googleapis.com/${bucketName}/${fileName}`;
        logging.log(`Text-to-speech audio saved to GCS: ${publicUrl}`);
        return publicUrl
    } catch (error) {
        logging.error(`Error in text-to-speech function: ${error.message}`);
    }
}

import { Transform, Duplex, pipeline } from "node:stream";
import WebSocket, { createWebSocketStream } from "ws";

// stream is a Stream from OpenAI returning text letter-by-letter
export default async function (
  {
    apiKey,
    voice_id,
    stream,
  }: NodeInputs,
  { logging, req: ctx }
: NodeScriptOptions) : NodeOutput  {
  const ws = new WebSocket(
    `wss://api.elevenlabs.io/v1/text-to-speech/${voice_id}/stream-input?model_id=eleven_monolingual_v1`
  );
  const wsStream = createWebSocketStream(ws, { encoding: "utf-8" });

  const from11LabsStream = new Transform({
    transform(chunk, encoding, callback) {
      const data = JSON.parse(chunk.toString());

      if (data.audio) {
        this.push(Buffer.from(data.audio ?? "", "base64"));
      }
      callback(null);
    },
    final(callback) {
      wsStream.end();
      this.push(null);
      callback();
    }
  });

  const to11LabsStream = new Transform({
    transform(chunk, encoding, callback) {
      chunk = chunk.toString();
      const lastChar = chunk.slice(-1);
      if (this.word === undefined) {
        this.word = chunk;
        ws.send(JSON.stringify({
          text: " ",
          voice_settings: {
            stability: 0.8,
            similarity_boost: 0.8,
          },
          xi_api_key: apiKey,
        }))
        callback(null);
      } else if (["\n", "\r", " ", ".", "!", "?"].includes(lastChar)) {
        this.word += chunk;
        this.word = this.word.slice(-1) !== " " ? this.word + " " : this.word;
        logging.log("Pushing", this.word);
        ws.send(JSON.stringify({
          text: this.word,
          try_trigger_generation: true,
        }))
        callback(null);
        this.word = "";
      } else {
        this.word += chunk;
        callback(null);
      }
    },
    final(callback) {
      ws.send(JSON.stringify({ text: "" }));
      callback(null);
    },
  });

  pipeline(stream, to11LabsStream, (err) => {
    if (err) logging.log('Stream to 11Labs pipeline error', err);
    else logging.log('Stream to 11Labs pipeline completed');
  });

  pipeline(wsStream, from11LabsStream, (err) => {
    if (err) logging.log('11Labs to return stream error', err);
    else logging.log('11Labs to return stream pipeline completed');
  });

  return from11LabsStream;
}
