import {
    getApps,
    initializeApp
} from "firebase-admin/app";
import {
    getAuth
} from "firebase-admin/auth";

export default async function fetchFirebaseUserData({
    email,
    projectId
}: NodeInputs, {
    logging
}: NodeScriptOptions) : NodeOutput  {
    const alreadyCreatedApps = getApps();
    const app =
        alreadyCreatedApps.length === 0 ?
        initializeApp({
            projectId: projectId && projectId.length > 1 ? projectId.trim() : undefined,
        }) :
        alreadyCreatedApps[0];
    const auth = getAuth(app);

    try {
        const user = await auth.getUserByEmail(email);

        return user;
    } catch (error) {
        logging.log("Error fetching user data:", error);

        throw error; // Re-throw the error for handling at a higher level if needed
    }
}

import { Firestore, FieldValue } from '@google-cloud/firestore';

export default async function addObjectToArray({
  collectionName,
  documentID,
  fieldKey,
  object,
  projectId
}: NodeInputs) : NodeOutput  {
  try {
    const firestore = new Firestore(projectId && projectId.length > 1 ? {
      projectId: projectId.trim()
    } : undefined);

    const fieldValue = FieldValue;

    await firestore
      .collection(collectionName)
      .doc(documentID)
      .update({ [fieldKey]: fieldValue.arrayUnion(object) });

    return { success: true };
  }
  catch (e){
    return e;
  }
}

import { Firestore } from '@google-cloud/firestore';

export default async function firestoreQueryDocuments(
  {
    collection,
    filters = [],
    limit,
    orderBy = [],
    projectId // Optional project ID parameter
  }: NodeInputs,
  {}
: NodeScriptOptions) : NodeOutput  {
  const firestore = new Firestore(projectId && projectId.length > 1 ? {
    projectId: projectId.trim()
  } : undefined);

  let query = firestore.collection(collection);

  orderBy.forEach(orderObj => {
    for (const [field, direction] of Object.entries(orderObj)) {
      const lowerCaseDirection = direction.toLowerCase();
      if (lowerCaseDirection === 'asc' || lowerCaseDirection === 'desc')
        query = query.orderBy(field, lowerCaseDirection);
    }
  });

  // Validate that filters exist and each filter has necessary properties
  if (Array.isArray(filters)) {
    filters.forEach(filter => {
      if (filter.field && filter.operator && filter.hasOwnProperty('value')) {
        query = query.where(filter.field, filter.operator, filter.value);
      }
    });
  }

  let snapshot = await query.limit(limit ?? 100).get();
  return snapshot.docs.map(doc => ({
    id: doc.id,
    data: doc.data()
  }));
}

