import { Firestore, DocumentReference } from '@google-cloud/firestore';

function convertDataToReferences(firestore: Firestore, data: Record<string, any>): Record<string, any> {
  const convertedData: Record<string, any> = {};
  for (const key in data) {
    const value = data[key];
    if (typeof value === 'string' && value.includes('/')) {
      try {
        const ref = firestore.doc(value);
        if (ref instanceof DocumentReference) {
          convertedData[key] = ref;
        } else {
          convertedData[key] = value;
        }
      } catch (error) {
        convertedData[key] = value;
      }
    } else if (typeof value === 'object' && value !== null) {
      convertedData[key] = convertDataToReferences(firestore, value);
    } else {
      convertedData[key] = value;
    }
  }
  return convertedData;
}

export default async function createDocument({
  collectionName,
  id,
  data,
  merge,
  projectId
}: NodeInputs): NodeOutput {
  console.log("createDocument");
  const firestore = new Firestore(
    projectId && projectId.length > 1 ? { projectId: projectId.trim() } : undefined
  );

  const convertedData = convertDataToReferences(firestore, data);

  if (id) {
    await firestore.collection(collectionName).doc(id).set(convertedData, { merge });
    return {
      id,
      path: `${collectionName}/${id}`,
      status: 'success!'
    };
  } else {
    const ref = await firestore.collection(collectionName).add(convertedData);
    return {
      id: ref.id,
      path: ref.path,
      status: 'success!'
    };
  }
}

