import { Firestore, DocumentReference, Filter } from "@google-cloud/firestore";

export default async function firestoreQueryDocuments({
  collection,
  filters = {},
  limit,
  orderBy = [],
  projectId
}: NodeInputs) : NodeOutput  {
  const firestore = new Firestore(projectId && projectId.length > 1 ? {
    projectId: projectId.trim()
  } : undefined);
  let query = firestore.collection(collection);

  orderBy.forEach((orderObj) => {
    for (const [field, direction] of Object.entries(orderObj)) {
      const lowerCaseDirection = direction.toLowerCase();
      if (
        "asc".includes(lowerCaseDirection) ||
        "desc".includes(lowerCaseDirection)
      ) {
        query = query.orderBy(field, lowerCaseDirection);
      }
    }
  });

  if (filters && Object.keys(filters).length > 0) {
    const combinedFilter = constructFilterTree(filters, firestore);
    if (combinedFilter) {
      query = query.where(combinedFilter);
    }
  }

  let snapshot = await query.limit(limit || 100).get();

  return snapshot.docs.map((doc) => ({
    id: doc.id,
    data: doc.data(),
  }));
}

function constructFilterTree(filter, firestore) {
  if (!filter.logic) {
    let filterValue = filter.value;

    // Check if filterValue is a potential Firestore reference
    if (typeof filterValue === 'string' && filterValue.includes('/')) {
      try {
        const ref = firestore.doc(filterValue);
        if (ref instanceof DocumentReference) {
          filterValue = ref;
        }
      } catch (error) {
        return; // Skip this filter if the reference is invalid
      }
    }

    return Filter.where(filter.field, filter.operator, filterValue);
  }

  const combinedFilters = filter.filters
    .map(subFilter => constructFilterTree(subFilter, firestore))
    .filter(subFilter => subFilter !== undefined);

  return filter.logic === "OR"
    ? Filter.or(...combinedFilters)
    : Filter.and(...combinedFilters);
}

import { Firestore } from '@google-cloud/firestore';

export default async function getFieldValue({
  collectionName,
  id,
  fieldKey,
  projectId
}: NodeInputs) : NodeOutput  {
  const firestore = new Firestore(projectId && projectId.length > 1 ? {
    projectId: projectId.trim()
  } : undefined);

  const docRef = firestore.collection(collectionName).doc(id);
  const docSnapshot = await docRef.get();

  if (docSnapshot.exists) {
    const data = docSnapshot.data();
    if (data && data[fieldKey] !== undefined) {
      return {
        id,
        fieldKey,
        value: data[fieldKey],
        status: 'success!'
      };
    } else {
      return {
        id,
        fieldKey,
        status: 'Field not found in the document'
      };
    }
  } else {
    return {
      id,
      fieldKey,
      status: 'Document not found'
    };
  }
}

