export default async function updateFirestoreDocument({
    docPath,
    newDocument,
    projectId
}: NodeInputs, {
    logging
}: NodeScriptOptions) : NodeOutput  {
    const firestore = new Firestore(projectId && projectId.length > 1 ? {
        projectId: projectId.trim()
    } : undefined);

    const docRef = firestore.doc(docPath);

    const result = await docRef.set(newDocument, {
        merge: true
    });
    return result
}

import { Firestore } from '@google-cloud/firestore';

function createNestedObject(path, value) {
  let obj = value;
  for (let i = path.length - 1; i >= 0; i--) {
    obj = { [path[i]]: obj };
  }
  return obj;
}

export default async function updateFirestoreFieldValue({
  docPath,
  fieldKey,
  newValue,
  projectId
}: NodeInputs,{logging}: NodeScriptOptions) : NodeOutput  {
  const firestore = new Firestore(projectId && projectId.length > 1 ? {
    projectId: projectId.trim()
  } : undefined);

  const docRef = firestore.doc(docPath);

  const fieldKeys = fieldKey.split('.');
  const dataObject = createNestedObject(fieldKeys, newValue);

  const result = await docRef.set(dataObject, { merge: true });
  return result
}
