import fetch from 'node-fetch';

export default async function createGoogleCalendarEvent({ 
  calendarId, 
  conferenceDataVersion, 
  sendUpdates,
  supportsAttachments,
  start,
  end,
  attachments,
  attendees,
  description,
  summary
}: NodeInputs, { queryParams = {}, auth }: NodeScriptOptions) : NodeOutput  {
    const { access_token } = await auth.getToken();
    const query = new URLSearchParams({
        conferenceDataVersion,
        sendUpdates,
        supportsAttachments,
        ...queryParams
    });

    const url = `https://www.googleapis.com/calendar/v3/calendars/${calendarId}/events?${query.toString()}`;

    const response = await fetch(url, {
        method: 'POST',
        headers: {
            Authorization: `Bearer ${access_token}`,
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ start, end, attachments, attendees, description, summary })
    });
    return response.json();
}

export default async function deleteGoogleCalendarEvent({
    calendarId,
    eventId,
    sendUpdates
}: NodeInputs, {
    auth
}: NodeScriptOptions) : NodeOutput  {
    const {
        access_token
    } = await auth.getToken();
    const response = await fetch(`https://www.googleapis.com/calendar/v3/calendars/${calendarId}/events/${eventId}?sendUpdates=${sendUpdates}`, {
        method: 'DELETE',
        headers: {
            'Authorization': `Bearer ${access_token}`
        }
    });
    if (!response.ok) throw new Error('Failed to delete event');
    return {
        success: true,
        message: 'Event deleted successfully'
    };
}

export default async function fetchGoogleCalendarEvent({
    calendarId,
    eventId
}: NodeInputs,{
  auth
}: NodeScriptOptions) : NodeOutput  {
    const { access_token } = await auth.getToken();
    const response = await fetch(`https://www.googleapis.com/calendar/v3/calendars/${calendarId}/events/${eventId}`, {
      method: 'GET',
      headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${access_token}`
        }
    });
    const data = await response.json();
    return data;
}

import fetch from 'node-fetch';
export default async function fetchGoogleCalendarEvents({
    calendarId,
    timeMin,
    timeMax
}: NodeInputs, {
  auth
}: NodeScriptOptions) : NodeOutput  {
    const { access_token } = await auth.getToken();
    let addRange;

    if(timeMin && timeMax){
      addRange = `&timeMin=${encodeURIComponent(timeMin)}&timeMax=${encodeURIComponent(timeMax)}`
    }
    const response = await fetch(`https://www.googleapis.com/calendar/v3/calendars/${calendarId}/events?orderBy=startTime&singleEvents=true&sortOrder=descending${addRange}`, {
      method: 'GET',
      headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${access_token}`
        }
    });
    const data = await response.json();
    return data;
}

