import fetch from 'node-fetch';

export default async function updateGoogleCalendarEvent({
    calendarId,
    eventId,
    conferenceDataVersion,
    maxAttendees,
    sendUpdates,
    supportsAttachments,
    start,
    end,
    attachments,
    attendees,
    description,
    summary
}: NodeInputs, {
    auth
}: NodeScriptOptions) : NodeOutput  {
    const {
        access_token
    } = await auth.getToken();
    const query = new URLSearchParams({
        conferenceDataVersion,
        maxAttendees,
        sendUpdates,
        supportsAttachments
    });

    const response = await fetch(`https://www.googleapis.com/calendar/v3/calendars/${calendarId}/events/${eventId}?${query.toString()}`, {
        method: 'PATCH',
        headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${access_token}`
        },
        body: JSON.stringify({
            start,
            end,
            attachments,
            attendees,
            description,
            summary
        })
    });

    return response.json();
}
