import fetch from 'node-fetch';
export default async function getSupabaseTableRow({
    supabaseUrl,
    supabaseKey,
    table,
    filter
}: NodeInputs) : NodeOutput  {
    const url = `${supabaseUrl}/rest/v1/${table}?${encodeURI(filter)}`;

    const headers = {
        'apikey': supabaseKey,
    };

    const requestOptions = {
        method: 'GET',
        headers
    };

    const response = await fetch(url, requestOptions);
    const data = await response.json();

    return data;
}

import fetch from 'node-fetch';
export default async function readSupabaseTableColumns({
    table,
    supabaseUrl,
    supabaseKey,
    columns
}: NodeInputs, { logging }: NodeScriptOptions) : NodeOutput  {
    try{
        const selectColumns = columns ? columns.replace(/\s+/g, "") : "*";
        logging.log(selectColumns)
        const response = await fetch(`${supabaseUrl}/rest/v1/${table}?select=${selectColumns}`, {
        headers: {
            'apikey': supabaseKey,
            'Content-Type': 'application/json'
        }
    });

    const data = await response.json();
    return data;
    }
    catch(e){
        return e;
    }
}

import fetch from 'node-fetch';
export default async function supabaseInsertObject({ table, token, objectData, supabaseUrl, supabaseKey }: NodeInputs, { logging }: NodeScriptOptions) : NodeOutput  {
    const supaUrl = `${supabaseUrl}/rest/v1/${table}`;

    const headers = {
    'apikey': supabaseKey,
    'Authorization': `Bearer ${token}`,
    'Content-Type': 'application/json',
    'Prefer': 'return=representation',
    };

    const requestData = objectData;

    try {
        const response = await fetch(supaUrl, {
            method: 'POST',
            headers: headers,
            body: JSON.stringify(requestData)
        });

        if (!response.ok) {
            const errorData = await response.json();
            logging.error('Error:', errorData);
            return errorData;
        }

        const data = await response.json();
        logging.log('Response:', data);
        return data;
    } catch (error) {
        logging.error('Error:', error);
        return error;
    }
}

import fetch from 'node-fetch';

export default async function deleteSupabaseTableRow({
    table,
    supabaseUrl,
    supabaseKey,
    filter
}: NodeInputs) : NodeOutput  {
    // Construct the URL with the table and filter
    const url = `${supabaseUrl}/rest/v1/${table}?${encodeURI(filter)}`;

    // Set the headers for the request
    const headers = {
        'apikey': supabaseKey,
        'Content-Type': 'application/json'
    };

    // Create the DELETE request options
    const requestOptions = {
        method: 'DELETE',
        headers
    };

    // Send the DELETE request
    const response = await fetch(url, requestOptions);

    if (response.ok) {
        // Request was successful, you can handle the success as needed
        return { success: true, message: 'Row deleted successfully' };
    } else {
        // Handle HTTP error responses
        throw new Error(`Failed to delete row. Status: ${response.status}`);
    }
}

