import {
    createClient
} from '@supabase/supabase-js';
export default async function callPostgresFunction({
    apiKey,
    apiUrl,
    functionName,
    args
}: NodeInputs) : NodeOutput  {
    const supabase = createClient(apiUrl, apiKey);
    const {
        data,
        error
    } = await supabase.rpc(functionName, args);
    if (error) throw error;
    return data;
}

